import {createSelector, createEntityAdapter} from "@reduxjs/toolkit";
import {apiSlice} from '../api/api'

const drugsAdapter = createEntityAdapter({
    sortComparer: (a, b) => b.id.localeCompare(a.id)
})

const initialState = drugsAdapter.getInitialState()

export const drugsApiSlice = apiSlice.injectEndpoints({
       endpoints: builder => ({
        getDrugs: builder.query({
            query: () => '/',
            transformResponse: responseData => {
                return drugsAdapter.setAll(initialState, responseData.drugs)
            },
            providesTags: { type: 'Drug', id: "LIST" },
        }),
        // toggleFavorite: builder.mutation({
        //     query: ({initialItem, favorite}) => ({
        //         url: '/favorite',
        //         method: 'PUT',
        //         body: {
        //             ...initialItem,
        //             favorite: favorite
        //         }
        //     }),
        //     invalidatesTags: (result, error, arg) => [
        //         { type: 'Drug', favorite: arg.favorite }
        //     ]
        // }),
        addFavorite: builder.mutation({
            query: ({ initialItem, favorite}) => ({
                url: `/favorite`,
                method: 'PATCH',
                body: { favorite }
            }),
            async onQueryStarted({ initialItem, favorite}, { dispatch, queryFulfilled }) {
                const patchResult = dispatch(
                     extendedApiSlice.util.updateQueryData('getDrugs', 'getDrugs', draft => {
                        const drug = draft.entities[initialItem.id]
                        if (drug) initialItem.favorite = favorite
                    })
                )
                try {
                    await queryFulfilled
                } catch {
                    patchResult.undo()
                }
            }
        })
    })
})
export const {
    useGetDrugsQuery,
    useToggleFavoriteMutation
} = drugsApiSlice

