import React, {useEffect} from 'react'
import { useGetCartQuery, selectAllCart } from '../api/cartSlice'
import { useCopyCouponMutation, selectCouponById, selectAllCoupons } from '../api/couponsSlice'
import CartItem from './CartItem'
import { useSelector } from "react-redux"

export const ShoppingCart = ({setCartTotal, setCart}) => {

  const coupons = useSelector(selectAllCoupons)
  let couponsSelected = [];
  for (let i = 0; i < coupons.length; i++) {
    if(coupons[i].copied === true){
      couponsSelected.push = coupons[i]
      break;
    }
  }
  console.log(couponsSelected);
  const cart = useSelector(selectAllCart)
  const contentCart = cart.map(cartItemId=> <CartItem key={cartItemId.id} cartItemId={cartItemId}/>)
  function total(){
    let temp = cart.map(function(item){
      return item.price*item.quantity
    })
    let sum = temp.reduce(function(prev, next){
      return prev+next
    }, 0)
    return sum
  }
  let totalCart = total(); 
  localStorage.setItem('cart', JSON.stringify(cart));

  useEffect(() => {
    setCartTotal(totalCart);
    const cartToSave = JSON.stringify(cart)
    setCart(cartToSave)
  }, [totalCart, cart])
  // setCartTotal(totalCart);
  // const cartToSave = JSON.stringify(cart)
  // setCart(cartToSave)

  return (
    <div className='shoppingCart'>
      <div className='cartTitle'>
          <div>Medicine</div>
          <div>Price</div>
          <div>Quantity</div>
          <div>Shop</div>
          <div>Total</div>
          <div></div>
      </div>
      <div>{contentCart}</div>
      <div className='cartTotal'><div>{'Total:'}</div><div>{totalCart.toFixed(2)}</div></div>
    </div>

  )
}
