import React, {useEffect, useState} from 'react'
import { useSelector } from "react-redux";
import { useGetShopsQuery} from '../api/shopsSlice'
import { useGetDrugsQuery} from '../api/drugsSlice'
import Shop from './Shop';



const ShopsList = () => {

    const { data:shops, isLoadingShops, isSuccessShops, isErrorShops, errorShops } = useGetShopsQuery('getShops')
    const { data:drugs, isLoadingDrugs, isSuccessDrugs, isErrorDrugs, errorDrugs } = useGetDrugsQuery('getDrugs')

    let content;
    if (isLoadingShops) {
        content = <p>"Loading..."</p>;
    } else if (isSuccessShops) {
        content = shops.ids.map(shopId => <Shop key={shopId} shopId={shopId}/>)
    } else if (isErrorShops) {
        content = <p>{errorShops}</p>;
    }

    // console.log(shops);

    return(
      <div className='mainPage'>
        <div className='shopsMenu'>
          <h2 className='shopsTitle'>Shops:</h2>
          {content}
        </div>
        <div className='drugsMenu'>
          MedicinesMenu
        </div>
      </div>


    );
}

export default ShopsList
