import {createSelector, createEntityAdapter} from "@reduxjs/toolkit";
import {apiSlice} from './api'
import { quartersInYear } from "date-fns/constants";

const cartAdapter = createEntityAdapter()

const initialState = cartAdapter.getInitialState()

export const cartApiSlice = apiSlice.injectEndpoints({
       endpoints: builder => ({
        getCart: builder.query({
            query: () => '/cart',
            transformResponse: responseData => {
                return cartAdapter.setAll(initialState, responseData.cart)
            },
            providesTags: { type: 'Cart', id: "LIST" },
            // providesTags: (result, error, arg) => [
            //     {type: 'Cart', id: "LIST"},
            //     ...result.ids.map(({ id }) => ({ type: 'Cart', id }))
            // ]
        }),
        addToCart: builder.mutation({
            query: itemToAdd => ({
                url: '/cart',
                method: 'POST',
                body: itemToAdd
            }),
            invalidatesTags: [
                { type: 'Cart', id: "LIST" }
            ]
        }),
        increaseQty: builder.mutation({
            query: initialItem => ({
                url: `/cart`,
                method: 'PUT',
                body: {
                    ...initialItem,
                    quantity: Number(initialItem.quantity) + 1
                }
            }),
            invalidatesTags: (result, error, arg) => [
                { type: 'Cart', id: arg.id }
            ]
        }),
        decreaseQty: builder.mutation({
            query: initialItem => ({
                url: `/cart`,
                method: 'PUT',
                body: {
                    ...initialItem,
                    quantity: Number(initialItem.quantity) - 1
                }
            }),
            invalidatesTags: (result, error, arg) => [
                { type: 'Cart', id: arg.id }
            ]
        }),
        deleteFromCart: builder.mutation({
            query: ({ id }) => ({
                url: `/cart`,
                method: 'DELETE',
                body: { id }
            }),
            invalidatesTags: (result, error, arg) => [
                { type: 'Cart', id: arg.id }
            ]
        }),
        addCustomer: builder.mutation({
            query: customerData => ({
                url: '/customer',
                method: 'POST',
                body: customerData
            }),
            // invalidatesTags: [
            //     { type: 'Customer', id: "LIST" }
            // ]
        }),
    })
})
export const {
    useGetCartQuery,
    useAddToCartMutation,
    useDeleteFromCartMutation,
    useIncreaseQtyMutation,
    useDecreaseQtyMutation, 
    useAddCustomerMutation
} = cartApiSlice

