import { useGetDrugsQuery} from '../api/drugsSlice'
import {useAddToCartMutation} from '../api/cartSlice'
import {Buffer} from "buffer" 
import { useState } from 'react'
import { useGetShopsQuery } from '../api/shopsSlice'
import ReactDOM from 'react-dom'
// import { fas } from '@fortawesome/free-solid-svg-icons'
// import { far } from '@fortawesome/free-regular-svg-icons'
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import { faStar } from '@fortawesome/free-solid-svg-icons'
import { StarEmpty, StarSolid } from './Favorites'
// import StarSVG from './star.svg'


const Drug = ({drugId}) => {
    // const glasses = findIconDefinition({ prefix: 'fas', iconName: 'glasses' })
    // const glassesIcon = icon(glasses)
    const [quantity, setQuantity] = useState(1)
    const [favorite, setFavorite] = useState(false)
    const qtyDecrease = () => {
        if (quantity === 1) {
            return;
        }
        setQuantity(quantity - 1)
    }
    const qtyIncrease = () => setQuantity(quantity + 1)

    const { drug } = useGetDrugsQuery('getDrugs', {
        selectFromResult: ({ data }) => ({
            drug: data?.entities[drugId]
        }),
    })
    const { shop } = useGetShopsQuery('getShops', {
        selectFromResult: ({ data }) => ({
            shop: data?.entities[drug?.shop_id]
        }),
    })
    const base64 = Buffer.from(drug?.img.data, "binary" ).toString("base64");

    const idInCart = `${drug.id}${new Date().getTime()}`
    const [addToCart, { isLoading }] = useAddToCartMutation()

    const onAddItemClicked = async () => {
        if (!isLoading) {
            try {
                await addToCart({ id: idInCart, name: drug.name, shop: drug.shop_id, quantity: quantity, price: drug.price }).unwrap()
            } catch (err) {
                console.error('Failed to add the item', err)
            }
        }
    }
    const addFav = () => {
        const addFavDiv = document.getElementsByClassName('shown')[drugId]
        console.log(addFavDiv);
        addFavDiv.className = 'hidden'
        // addFavDiv.classList.remove('shown')
        // addFavDiv.classList.add('hidden')
    }
    const removeFav = () => {
        const removeFavDiv = document.getElementsByClassName('hidden')[drugId]
        removeFavDiv.className = 'shown'
        // removeFavDiv.classList.remove('hidden')
        // removeFavDiv.classList.add('shown')
    }
    const toggleFavorite = () => {
        setFavorite(!favorite)
        if (favorite) {
            addFav()
        } else {
            removeFav()
        }
    }

  return (
    <article className='drugItem'>
        <div className='favIcon' onClick={toggleFavorite}>
            <div className='shown' onClick={addFav}><StarEmpty /></div>
            <div className='hidden' onClick={removeFav}> <StarSolid /></div>

        </div>
        <img className='drugPic' src={`data:image/png;base64,${base64}`}/>
        <div className='picDescription'>
            <h2>{drug.name}</h2>
            <p>{`${drug.price} uah`}</p>
            <h2>{shop.name}</h2>
            <div className='qtyCounter'>
                <button className='qtyButton' onClick={qtyDecrease}>{'-'}</button>
                <div>{quantity}</div>
                <button className='qtyButton' onClick={qtyIncrease}>{'+'}</button>
            </div>

            <button className='buyButton' type="button" onClick={onAddItemClicked}>{'add to Cart'}</button>
        </div>



    </article>
  )
}

export default Drug
