import React from 'react'
import { useEffect } from'react'

export const Captcha = () => {

    let captcha;
    useEffect(() => {
        Generate();
    }, [])

function Generate() {
    document.getElementById("submit").value = "";
    captcha = document.getElementById("image");
    let uniquechar = "";
    const randomchar = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (let i = 1; i < 5; i++) {
        uniquechar += randomchar.charAt(Math.random() * randomchar.length)
    }
    captcha.innerHTML = uniquechar;
}

function Printmsg() {
    const usr_input = document.getElementById("submit").value;
    if (usr_input == captcha.innerHTML) {
        let s = document.getElementById("key").innerHTML = "Matched";
        Generate();
    }
    else {
        let s = document.getElementById("key").innerHTML = "not Matched";
        Generate();
    }
}
  return (
    <div>
        <div id="user-input" className="inline">
            <input type="text" id="submit" placeholder="Captcha code" />
        </div>
        <div className="inline" onclick={Generate}>
            <i className="fas fa-sync"></i>
        </div>
        <div id="image" className="inline" selectable="False"></div>
        <input type="submit" id="btn" onclick={Printmsg} />
        <p id="key"></p>
    </div>
  )
}
