import React, {useEffect} from 'react'
import { useGetCartQuery, selectAllCart } from '../api/cartSlice'
import CartItem from './CartItem'
import { useSelector } from "react-redux"

export const ShoppingCart = ({setCartTotal}) => {

  const cart = useSelector(selectAllCart)
  const contentCart = cart.map(cartItemId=> <CartItem key={cartItemId.id} cartItemId={cartItemId}/>)
  let total = 0; 

    for(let i=0; i<cart.length; i++){
  total = total + cart[i].price * cart[i].quantity
    }


  return (
    <div className='shoppingCart'>
      <div className='cartTitle'>
          <div>Medicine</div>
          <div>Price</div>
          <div>Quantity</div>
          <div>Shop</div>
          <div>Total</div>
          <div></div>
      </div>
      <div>{contentCart}</div>
      <div className='cartTotal'><div>{'Total:'}</div><div>{""&&total.toFixed(2)}Total amount</div></div>
    </div>

  )
}
