import React from 'react'
import { InputForm } from './InputForm'
import { ShoppingCart } from './ShoppingCart'
import { useAddCustomerMutation } from '../api/cartSlice'
import { useState } from "react"

export const FinalPage = () => {
  const [addCustomer, { isLoading }] = useAddCustomerMutation()

  // const [name,email, phone, adress] = document.forms[0]
  const [nameF, setNameF] = useState(document.forms[0].name.value)
  const [emailF, setEmailF] = useState(document.forms[0].email.value)
  const [phoneF, setPhoneF] = useState(document.forms[0].email.value)
  const [adressF, setAdressF] = useState(document.forms[0].adress.value)

  const onSubmitClicked = async () => {

    if(!isLoading){
      try {
        const [name,email, phone, adress] = document.forms[0]

        // console.log(name);

        await addCustomer({ name: name.value ,email:email.value, phone:email.value, adress:adress.value}).unwrap()
        name.value = ''
        setName('')
        setEmail('')
        setPhone('')
        setAdress('')
    } catch (err) {
          console.error('Failed to summbit the purchase', err)
    }
    }

}

  return (
    <>
      <div className='finalPage'>

        <InputForm nameF={nameF} emailF={emailF} phoneF={phoneF} adressF={adressF}/>
        <ShoppingCart /> 

      </div>
      <button className="submitButton" type="button" onClick={onSubmitClicked}>Submit</button>
    </>
  )
}
