import React from 'react'
import { useEffect, useState } from'react'

export const Captcha = () => {

let [keyText, setKeyText] = useState('');
let [captcha, setCaptcha] = useState('');

    // let captcha;
    useEffect(() => {
        Generate();
    }, [])

function Generate() {
    document.getElementById("submit").value = "";
    // captcha = document.getElementById("captcha_image");
    let uniquechar = "";
    const randomchar = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (let i = 1; i < 5; i++) {
        uniquechar += randomchar.charAt(Math.random() * randomchar.length)
    }
    setCaptcha(uniquechar)
    // captcha.innerHTML = uniquechar;
}

function Printmsg() {
    const usr_input = document.getElementById("submit").value;
    if (usr_input == captcha.innerText) {
        setKeyText("Matched");
        Generate();
    }
    else {
        setKeyText("not Matched");
        Generate();
    }
}
  return (
    <div>
        <div id="captcha_input" className="inline">
            <input type="text" id="submit" placeholder="Captcha code" />
        </div>
        <div className="inline" onClick={Generate}>
            <i className="fas fa-sync"></i>
        </div>
        <div id="captcha_image" className="inline" selectable="False">{captchaImage}</div>
        <input type="submit" id="btn" onClick={Printmsg} />
        <p id="key">{keyText}</p>
    </div>
  )
}
