import ShopsList from "../shops/ShopsList"
import { useGetDrugsShopsQuery, selectDtSById} from '../api/drugsToShopsSlice'
import { useGetDrugsQuery, selectAllDrugs} from '../api/drugsSlice'
import Drug from "../drugs/Drug"
import { useMemo } from'react'
import { useSelector } from "react-redux"

export const DrugsList = ({shop}) => {

  let drugsList;
  // const { data:drugsShops, isLoading:isLoadingD, isSuccess:isSuccessD} = useGetDrugsShopsQuery(shop)
  // const { data:drugs, isLoading, isSuccess} = useGetDrugsQuery('getDrugs')
  const drugs = useSelector(selectAllDrugs)
  // const drugsShops = useSelector(selectDtSById)
  const drugsShops = useSelector((state) => selectDtSById(state, Number(shop.id)))
  console.log(drugs);
  console.log(drugsShops);

  // if (isLoadingD) {
  //   drugsList = <p>"Loading..."</p>;
  // } else if (isSuccessD) {
    if(shop === 'default'){


      drugsList = drugs?.map(drugId => <Drug drugId={drugId} key={drugId} />);
    }
    else{
      drugsList = drugsShops?.map(drugId => <Drug drugId={drugId} key={drugId} />);
    }
  // } 


  return (
    <div className='drugsMenu'>
      {drugsList}
    </div>
       )
}
