import ShopsList from "../shops/ShopsList"
import { useGetDrugsShopsQuery} from '../api/drugsToShopsSlice'
import { useGetDrugsQuery} from '../api/drugsSlice'
import Drug from "../drugs/Drug"

export const DrugsList = (shop) => {

  let drugsList;
  const { data:drugsShops, isLoading:isLoadingD, isSuccess:isSuccessD} = useGetDrugsShopsQuery(shop.shop)

  // let drugsForShop;
  // if (isLoadingD) {
  //   drugsForShop = <p>"Loading..."</p>;
  // } else if (isSuccessD) {
  //   drugsForShop = drugsShops.ids;
  // }

  const { data:drugs, isLoading, isSuccess} = useGetDrugsQuery('getDrugs')

  if (isLoading) {
    drugsList = <p>"Loading..."</p>;
  } else if (isSuccess&&isSuccessD) {
    if(shop.shop === 'default'){
      drugsList = drugs.ids.map(drugId => <Drug key={drugId} drugId={drugId}/>);
    }
    else{
      drugsList = drugsShops.ids.map(drugId => <Drug key={drugId} drugId={drugId}/>);
    }
  } 
drugsList.ids.map(item=>console.log(item))

  console.log(drugsList);

  return (
    <div className='drugsMenu'>
      {drugsList}
    </div>
       )
}
