import React, {useEffect} from 'react'
import { useGetCartQuery } from '../api/cartSlice'
import CartItem from './CartItem'
import { useSelector } from "react-redux"

export const ShoppingCart = ({setCartTotal}) => {

  // const { data:cart, isLoading, isSuccess, isError, error } = useGetCartQuery('getCart')

  const cart = useSelector(selectAllCart)

  // let contentCart;
  // if (isLoading) {
  //   contentCart = <p>"Loading..."</p>;
  // } else if (isSuccess) {
    const contentCart = cart.map(cartItemId=> <CartItem key={cartItemId} cartItemId={cartItemId}/>)
  // }

  let total = 0; 
  if (isLoading) {
    total = <p>"Calculating..."</p>;
  } else if (isSuccess) {
    for(let i=0; i<cart.ids.length; i++){
      total = total + cart.entities[cart.ids[i]].price * cart.entities[cart.ids[i]].quantity
      setCartTotal(total)
    }
  }

  useEffect(() => {
    setCartTotal(total)
  }, []);




  return (
    <div className='shoppingCart'>
      <div className='cartTitle'>
          <div>Medicine</div>
          <div>Price</div>
          <div>Quantity</div>
          <div>Shop</div>
          <div>Total</div>
          <div></div>
      </div>
      <div>{contentCart}</div>
      <div className='cartTotal'><div>{'Total:'}</div><div>{""&&total.toFixed(2)}Total amount</div></div>
    </div>

  )
}
