import ShopsList from "../shops/ShopsList"
import { useGetDrugsShopsQuery} from '../api/drugsToShopsSlice'
import { useGetDrugsQuery} from '../api/drugsSlice'
import Drug from "../drugs/Drug"

export const DrugsList = (shop) => {

  // console.log(shop.shop);

  const { data:drugsToShop, isLoadingDS, isSuccessDS, isErrorDS, errorDS } = useGetDrugsShopsQuery(shop.shop)

  let drugsForShop;
  if (isLoadingDS) {
    drugsForShop = <p>"Loading..."</p>;
  } else if (isSuccessDS) {
    drugsForShop = <p>"Loading..."</p>;
    // drugsForShop = drugsToShop.ids.map(id => <Drug key={id} drugId={id}/>);
  }

  const { data:drugs, isLoading, isSuccess, isError, error } = useGetDrugsQuery('getDrugs')
  let drugsList;
  if (isLoading) {
    drugsList = <p>"Loading..."</p>;
    console.log('Loading');
  } else if (isSuccess) {
    if(shop.shop === 'default'){
      console.log('shopslist');
      drugsList = drugs.ids.map(drugId => <Drug key={drugId} drugId={drugId}/>);
    }
    else{
      console.log('drugsForShop');
      console.log(drugsForShop);
      //DrugsToShops
      drugsList = drugs.ids.map(drugId => <Drug key={drugId} drugId={drugId}/>);
    }
  } 

// console.log(drugsList);
  return (
    <div className='drugsMenu'>
      {drugsList}
      {/* {drugsForShop} */}
    </div>
       )
}
