import { InputForm } from './InputForm'
import { ShoppingCart } from './ShoppingCart'
import { useAddCustomerMutation } from '../api/cartSlice'
import React, { createContext, useContext, useState } from "react"

const NameContext = createContext();
// const useName = () => useContext(NameContext);
const EmailContext = createContext();
// const useEmail = () => useContext(EmailContext);
const PhoneContext = createContext();
// const usePhone = () => useContext(PhoneContext);
const AdressContext = createContext();
// const useAdress = () => useContext(AdressContext);

export const FinalPage = () => {
  const [name, setName] = useState('name');

  const [addCustomer, { isLoading }] = useAddCustomerMutation()
  const onSubmitClicked = async () => {
    if(!isLoading){
      try {
        const [name,email, phone, adress] = document.forms[0]
        await addCustomer({ name: name.value ,email:email.value, phone:phone.value, adress:adress.value}).unwrap()
        // setName('');
      } catch (err) {
          console.error('Failed to summbit the purchase', err)
      }
    }
}

  return (
    <NameContext.Provider value={name}>

    <div className='finalPage'>
      <InputForm setName={setName}/>
      <ShoppingCart /> 

    </div>
          <button className="submitButton" type="button" onClick={onSubmitClicked}>Submit</button>
    </NameContext.Provider>
  )
}

// export {NameContext, EmailContext, PhoneContext, AdressContext,useName,useEmail, usePhone,useAdress}
