import React from 'react'
import { useEffect, useState } from'react'

export const Captcha = ({setButtonStatus}) => {

let [keyText, setKeyText] = useState('');
let [captcha, setCaptcha] = useState('');
let [captchaCode, setCaptchaCode] = useState('');

useEffect(() => { Generate()}, [])
function Generate() {
    setCaptchaCode("")
    let uniquechar = "";
    const randomchar = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (let i = 1; i < 5; i++) {
        uniquechar += randomchar.charAt(Math.random() * randomchar.length)
    }
    setCaptcha(uniquechar)
}
const captchaChange = (e) => {
    setCaptchaCode(e.target.value)
}
function Printmsg() {
    const usr_input = captchaCode;
    if (usr_input == captcha) {
        setKeyText("Matched");
        setButtonStatus(false)
        Generate();
    }
    else {
        setKeyText("Not matched");
        Generate();
    }
}
  return (
    <div className="w-full gap-5 grid grid-rows-2 grid-cols-2 justify-items-center items-center">
        <div className="w-full text-dbeige">
            <input type="text" placeholder="Captcha code" value={captchaCode} onChange={captchaChange}className='w-full text-center'/>
        </div>
        <div className="border-2 p-2 font-semibold italic text-xl line-through select-none inline-block w-full text-center" selectable="False">{captcha}</div>
        <input type="submit" className='rounded p-3 w-full hover:cursor-pointer hover:shadow-sm hover:shadow-lyellow' onClick={Printmsg}/>
        <p className="w-full ">{keyText}</p>
        {/* conditional color ???*/}
    </div>
  )
}
