import { useGetShopsQuery} from '../api/shopsSlice'
import { NavLink } from "react-router-dom"
import { useGetDrugsQuery} from '../api/drugsSlice'
import styled from "styled-components"
import {useShopChosen} from '../mainPage/MainPage.js'

const NavUnlisted = styled.ul`

  // display: flex;
  padding: 0;
  // width: 300px;
  // justify-content: center;
  // align-items: center;
  // column-gap: 35px;
  margin:0;

  a {
    text-decoration: none;
  }

  li {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    color: darkslategrey;
    font-size: 1.3rem;
    list-style: none;
    width: 90%;
    height: 60px;
    text-align: center;
    line-height: 100%;
    border: 1px solid darkslategrey;
    border-radius: 10px;
    margin: 20px auto;
    background-color: white;
  }
  li:hover {
    background-color: antiquewhite;
    cursor: pointer;
  }
`;


const Shop = ({shopId}) => {
  let shopChosen = useShopChosen();
  console.log(shopChosen);

    const { shop } = useGetShopsQuery('getShops', {
        selectFromResult: ({ data }) => ({
            shop: data?.entities[shopId]
        }),
    })
    const onShopChosen = () => {
      shopChosen=shopId

  }

  return (
    <NavUnlisted>
      <NavLink to={`/shops/${shopId}`}
        style={({ isActive}) => {
          return {
            fontWeight: isActive ? "bold" : "normal",
          };
        }
      }
      onClick={onShopChosen}
      ><li>{shop.name}</li></NavLink>
    </NavUnlisted>
  )
}

export default Shop
