import { useGetShopsQuery} from '../api/shopsSlice'
import { NavLink } from "react-router-dom"
import styled from "styled-components";

const NavUnlisted = styled.li`

  list-style: none;

//   padding: 0;
  width: 300px;
  height: 200px;

//   margin:0;
  border: 1px solid darkslategrey;
  border-radius: 10px;

  a {
    text-decoration: none;
    color: darkslategrey;
    font-size: 1.3rem;
  }

//   li:hover {
//     background-color: antiquewhite;
//     cursor: pointer;
//   }
`;


const ShopExtended = ({shopId}) => {

    const { shop } = useGetShopsQuery('getShops', {
        selectFromResult: ({ data }) => ({
            shop: data?.entities[shopId]
        }),
    })


  return (
    <NavUnlisted>
      <NavLink to={`${shopId}`}
      className='shopDetailed'
        style={({ isActive}) => {
          return {
            fontWeight: isActive ? "bold" : "normal",
          };
        }}>
            <div className='shopTitle'>
                {shop.name}
            </div>
            <div className='shopAdress'>
                {shop.adress}
            </div>
            <div className='shopPhone'>{'+38 555-55-55'}</div>
        </NavLink>
    </NavUnlisted>
  )
}

export default ShopExtended
