import React, {useEffect, useState} from 'react'
import { useSelector } from "react-redux";
import { useGetShopsQuery, selectAllShops} from '../api/shopsSlice'
import Shop from './Shop';


const ShopsList = ({setShop}) => {
  // const { data: shops, isLoading, isError } = useGetShopsQuery()
  const [selectedShop, setSelectedShop] = useState({setShop})
  console.log({setShop});
  const shops = useSelector(selectAllShops)
  const chooseAllShops = () => { setSelectedShop('default') }
  let contentShops = shops.map(shopId => <Shop setShop={selectedShop} key={shopId.id} shopId={shopId}/>)


    return(

        <div className='text-center text-black shrink-0'>
          <button className='' onClick={chooseAllShops}>CHOOSE ALL:</button>
          {contentShops}
        </div>

    );
}

export default ShopsList
