import React from 'react'
import { selectAllCoupons, useGetCouponsQuery, selectCouponById } from '../api/couponsSlice'
import { useSelector } from "react-redux"
import { Coupon } from './Coupon'

export const CouponsList = () => {
  // const coupons = useSelector(selectAllCoupons)
  const { data:coupons, isLoading, isSuccess} = useGetCouponsQuery('getCoupons')

  const copyCoupon = (e) => {
    const couponSelected = useSelector((state) => selectCouponById(state, Number(e.target.parentElement.value)))
    // console.log(e.target.parentElement.value);
  }

  let couponsList;
  if (isSuccess) {
    couponsList = coupons.ids.map(id => <Coupon key={id} value={id}/>)
  }

  return (
    <div className='coupons'>
      <ul className='couponsList'>{couponsList}</ul>
    </div>

  )
}
