    setSortedList(true)
}
const resetSorting = () => {
  setSortedList(false)
}

  return (
    <>
      <div className='drugsMenu'>
        {/* {sortedList ? sortedDrugsList : drugsList} */}
        {drugsList}

      </div>
      <button className='buyButton' onClick={sortByPrice}>Sort by Price</button>
      <button className='buyButton' onClick={resetSorting}>Reset Sorting</button>
    </>
    )
}
