import { useGetShopsQuery, selectShopById} from '../api/shopsSlice'
// import { NavLink } from "react-router-dom"
// import { useGetMedsQuery} from '../api/medsSlice'
// import styled from "styled-components"
// import { createContext, useContext, useEffect } from "react"
// import { ShopChosen } from '../mainPage/MainPage'
import { useSelector } from 'react-redux'
import { Link } from "react-router-dom"


const Shop = ({shopId, setShop}) => {

const shop = useSelector((state) => selectShopById(state, Number(shopId.id)))

const onShopChanged = () => {setShop(shopId.id)}

  return (
    <Link to="/" className="focus:font-bold list-none px-2 md:px-5 my-1 md:my-3 hover:cursor-pointer" onClick={onShopChanged}><li>{shop.title}</li></Link>
    // <NavUnlisted>
    //   <NavLink to={`/`}

    //   ><li className="p-3 hover:bg-red hover:shadow-sm shadow-lbeige hover:cursor-pointer"></li></NavLink>
    // </NavUnlisted>
  )
}

export default Shop
